import React from 'react';
import styles from './layouts/DadosPessoais.module.css';
import {BsArrowRight} from 'react-icons/bs';
import fotoperfil from '../assets/fotoperfil.png'
import { useState } from 'react';
import useCep from 'react-hook-usecep'


function DadosPessoais() {

    const [rua, setRua] = useState();

    // const onSubmit = (e) => {
    //     e.preventDefault();
    //     const data = {
    //         nome: e.target.elements.nome.value,
    //         nacionalidade: e.target.elements.nacionalidade.value,
    //         idade: e.target.elements.idade.value,
    //         objetivo: e.target.elements.objetivo.value,
    //         email: e.target.elements.email.value,
    //         celular: e.target.elements.celular.value,
    //         cep: e.target.elements.cep.value,
    //         rua: e.target.elements.rua.value,
    //         bairro: e.target.elements.bairro.value,
    //         numero: e.target.elements.numero.value,
    //         cidade: e.target.elements.cidade.value,
    //         uf: e.target.elements.nome.value,
    //     }
    //     console.log(data)
    // }

    const checkCEP = (e) => {
        const cep = e.target.value
        
    fetch(`http://viacep.com.br/ws/${cep}/json/`)
    .then((res) => res.json())
    .then((res) => alert(res.logradouro + '\n' + res.bairro + '\n' + res.localidade + '\n' + res.uf));
    }

    return (

        
    <section className={styles.main}>
    <h2> Comece inserindo os <span className={styles.span1}>dados pessoais:</span></h2>

        <img src={fotoperfil} alt="logo" />
        <p className={styles.inseririmagem}>Inserir Imagem</p>
    
    <div className={styles.form}>
        <form /*onSubmit={onSubmit}*/>
            <h5>Nome</h5>
            <label className={styles.field1}>
                <input className={styles.nome} type='text' name='nome' placeholder=' Alan dos Santos Vicente'></input>
            </label>

            <div className={styles.titleform}>
            <h5>Nacionalidade</h5>
            <h5 className={styles.titleidade}>Idade</h5>
            </div>
            <label className={styles.field2}>
                <input className={styles.nacionalidade} type='text' name='nacionalidade' placeholder='Brasileiro'></input>
                <input className={styles.idade} type='number' name='idade' placeholder='28'></input>
            </label>

            <h5>Objetivo Profissional</h5>
            <label className={styles.field3}>
                <input className={styles.objetivo} type='text' name='objetivo' placeholder='Auxiliar Administrativo'></input>
            </label>    

            <h5>E-mail</h5>
            <label className={styles.field4}>
                <input className={styles.email} type='email' name='email' placeholder='alan.santos@gmail.com'></input>
            </label>

            <h5>Celular</h5>
            <label className={styles.field5}>
                <input className={styles.celular} type='number' name='celular' placeholder='11 96584 3654'></input>
            </label>

            <h5>CEP</h5>
            <label className={styles.field2}>
                <input className={styles.cep} type='number' onBlur={checkCEP} name='cep'  placeholder='65896-965'></input>
                <button className={styles.Button2}>Buscar CEP</button>
            </label>    

            <h5>Rua</h5>
            <label className={styles.field6}>
                <input className={styles.rua} type='text' name='rua' id='rua' placeholder='Av. Augusto Oscar' ></input>
            </label>


            <div className={styles.titleform}>
            <h5>Bairro</h5>
            <h5 className={styles.titlenumero}>Número</h5>
            </div>
            <label className={styles.field2}>
                <input className={styles.bairro} type='text' name='bairro'  placeholder='Jardim Paulista'></input>
                <input className={styles.numero} type='number' name='numero' placeholder='6586'></input>
            </label>


            <div className={styles.titleform}>
            <h5>Cidade</h5>
            <h5 className={styles.titleuf}>UF</h5>
            </div>
            <label className={styles.field2}>
                <input className={styles.cidade} type='text' name='cidade' placeholder='São Paulo'></input>
                <input className={styles.uf} type='text' name='uf' placeholder='SP'></input>
            </label>
            <button type="submit" className={styles.Button} >Avançar <span><BsArrowRight /> </span></button>      
        </form>
        
    </div>
    

    </section>
    )

}

export default DadosPessoais
